import { db } from '../database'

class LoginUsuario {
    constructor(usuario, password) {
        this.usuario = usuario
        this.password = password
        this.tipoUsuario = []
    }

    async comprobarTipoUsuario() {
        const tiposUsuarios = ['administradores', 'alumnos', 'profesores', 'familias']
        tiposUsuarios.forEach(tipo => {
            db.query(`SELECT * FROM  ${tipo} WHERE usuario = ?`, this.usuario, function(err, result) {
                if(result.length) {
                    this.tipoUsuario = this.tipoUsuario.push()
                }
            })
        })
    }
}

export default LoginUsuario;
